function gotoberanda() {
    $('html, body').animate({
        scrollTop: $("#beranda").offset().top
    }, 2000);
    scrollFunction();
};

function scrollFunction() {
    if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {
        $('.gotoup').style.display = "block";
      } else {
        $('.gotoup').style.display = "none";
      }
}

